package com.example.categories.controller;import com.example.categories.service.ProductCategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/productCategory")public class ProductCategoryController {    @Autowired    private ProductCategoryService ProductCategoryService;    @GetMapping("/category")    public ResponseEntity<?> getAllProducts() {        return ResponseEntity.ok(ProductCategoryService.getAllProducts());    }}